mapper.select:
SELECT
FROM COMPANY_INFO com
WHERE com.COMPANY_ID = #{id}

SELECT VERIFY_ID, VERIFY_TIME, VERIFY_END_TIME, OPER_USER_ID, COMPANY_ID FROM COMPANY_VERIFICAITON WHERE VERIFY_ID in
#{id}
AND IS_DELETE = 0

SELECT IND_NUM, IND_CHN, IND_ENG, IND_LEVEL, IND_PARENT_CODE, START_TIME FROM SYS_IND_CLASS WHERE IND_NUM in
#{id}
AND IS_DELETE = 0
