mapper.update:
UPDATE COMPANY_INFO         SET IS_DELETE = 1, IS_DELETE_TIME = NOW()
WHERE COMPANY_ID = #{id}

UPDATE COMPANY_INDUSTRY	    SET IS_DELETE = 1, IS_DELETE_TIME = NOW()
WHERE COMPANY_ID = #{id}

UPDATE COMPANY_VERIFICAITON SET IS_DELETE = 1, IS_DELETE_TIME = NOW()
WHERE COMPANY_ID = #{id}

UPDATE JOB_INFO			    SET IS_DELETE = 1, IS_DELETE_TIME = NOW()
WHERE COMPANY_ID = #{id}

UPDATE HR_INFO				SET IS_DELETE = 1, IS_DELETE_TIME = NOW()
WHERE COMPANY_ID = #{id}

mapper.select:
SELECT VERIFY_ID, VERIFY_TIME, VERIFY_END_TIME, OPER_USER_ID, COMPANY_ID FROM COMPANY_VERIFICAITON WHERE VERIFY_ID in
#{id}
AND IS_DELETE = 0

SELECT IND_NUM, IND_CHN, IND_ENG, IND_LEVEL, IND_PARENT_NUM, START_DATE FROM SYS_IND_CLASS WHERE IND_NUM in
#{id}
AND IS_DELETE = 0

SELECT HR_ID, COMPANY_ID, HR_ON_JOB, USER_ID FROM HR_INFO WHERE HR_ID in
#{id}
AND IS_DELETE = 0
