mapper.select:
SELECT
FROM JOB_INFO j
WHERE j.JOB_ID = #{id}

SELECT

FROM JOB_INFO j

WHERE
    j.IS_DELETE = 0 AND com.IS_DELETE = 0 AND h.IS_DELETE = 0 AND loc.IS_DELETE = 0

        AND j.JOB_ID =  #{jobId}


        AND j.JOB_NAME like "%"#{jobName}"%"


        AND j.COMPANY_ID = #{comId}


        AND com.COMPANY_NAME like "%"#{comName}"%"


        AND j.JOB_START_TIME  >=  #{startTime}


        AND j.JOB_END_TIME  <=  #{endTime}


        AND j.JOB_TYPE = #{type}


        AND j.JOB_SALARY_FLOOR  >=  #{salaryFloor}


        AND j.JOB_SALARY_CAP  <=  #{salaryCap}


        AND j.JOB_ACTIVE = #{active}


        AND  ( region.REGION_CHN like "%"#{location}"%" OR region.REGION_ENG like "%"#{location}"%" )


        AND degree.DEGREE_NUM IN

            #{item.degreeNum}



        AND class.IND_CODE IN

            #{item.indCode}
