mapper.insert:
INSERT INTO COM_RICH_TEXT (RELA_TYPE, RELA_ID, COMPILE_TYPE, JSON_CONTENT,TEXT_CONTENT)
       values(#{rela_type},#{rela_id},#{compile_type},#{json_content},#{text_content}
             )

mapper.update:
